function bindClickOrTouch(el, handler) {
  if (!el) return;
  let lastTouchTimestamp = 0;
  const wrapper = (e) => {
    e.stopPropagation();
    if (e.type === "touchend") lastTouchTimestamp = Date.now();
    else if (e.type === "click" && (Date.now() - lastTouchTimestamp) < 500) {
      e.preventDefault();
      return;
    }
    handler(e);
  };
  el.addEventListener("touchend", wrapper, { passive: false });
  el.addEventListener("click", wrapper);
}
function setupMenus() {
