};


function populateLanguages(langList) {
  if (!els.desktopDropdownOptions) return;

  els.desktopDropdownOptions.innerHTML = ""; // clear old items

  langList.forEach(lang => {
    const li = document.createElement("li");
    li.textContent = lang.name;
    li.dataset.langCode = lang.code;
    els.desktopDropdownOptions.appendChild(li);
