function setupValidation() {
    const applyError = (el, errorEl, msg) => {
        if (el) el.classList.toggle(CLASSES.invalid, !!msg);
        if (errorEl) errorEl.textContent = msg || "";
    };
    const validate = () => {
        if (hasUsernameBeenTouched) applyError(els.username, els.usernameError, els.username.value ? "" : "Enter your username.");
        if (hasPasswordBeenTouched) applyError(els.password, els.passwordError, els.password.value ? "" : "Enter your password.");
