    for (const key in IDS) els[key] = document.getElementById(IDS[key]);
    if (!els.userIcon) return false;

    console.log("Key element #userIcon found. Initializing application...");
    isAppInitialized = true;
    setOwnerSiteUrl();
    
    setupPasswordToggle();
    setupPasswordIconVisibility();
    setupMenus();
    setupValidation();
    const moxieKey = getMoxieKey(domain);
    populateLanguages();
    setupAllDropdowns();
    updateUI();

    const initialLangCode = getLanguagePreference();
    if (document.cookie.includes('langPref=')) {
        console.log(`Found language preference cookie: langPref=${initialLangCode}`);
    } else {
        console.log("No language preference cookie found. Defaulting to 'en'.");
    }

    updateLanguageDisplay(initialLangCode);
    applyMoxieTranslations();

    console.log("Application setup complete.");
    return true;
}

