module.exports = a = async ({ params }) => {
    console.log('params: ', params);

    try {
        // Normalize multi-value inputs
        let userEmails = [];
        let userPhones = [];

        if (typeof params.userEmails === 'string') {
            userEmails = params.userEmails.trim() === '' ? [] : JSON.parse(params.userEmails);
        } else if (Array.isArray(params.userEmails)) {
            userEmails = params.userEmails;
        }

        if (typeof params.userPhones === 'string') {
            userPhones = params.userPhones.trim() === '' ? [] : JSON.parse(params.userPhones);
        } else if (Array.isArray(params.userPhones)) {
            userPhones = params.userPhones;
        }

        // Normalize and prepend single-value inputs
        const emailPrimary = typeof params.userEmailPrimary === 'string' ? params.userEmailPrimary.trim() : null;
        const phonePrimary = typeof params.userPhonePrimary === 'string' ? params.userPhonePrimary.trim() : null;

        if (emailPrimary) userEmails.unshift(emailPrimary);
        if (phonePrimary) userPhones.unshift(phonePrimary);

        // Deduplicate and normalize
        userEmails = [...new Set(userEmails.map(e => e.toLowerCase()))];
        userPhones = [...new Set(userPhones)];

        let devices = params.devices;
        if (typeof devices === 'string') {
            devices = devices.trim() === '' ? [] : JSON.parse(devices);
        }

        if (!Array.isArray(userEmails) || !Array.isArray(userPhones)) {
            throw new Error('userEmails and userPhones must be arrays');
        }

        if (!Array.isArray(devices)) {
            throw new Error('devices must be an array');
        }

        const usableDevices = devices.filter(device =>
            device.status === 'ACTIVE' &&
            device.lock?.status === 'UNLOCKED' &&
            device.block?.status === 'UNBLOCKED'
        );

        const maskedUsableDevices = usableDevices.map(device => {
            const maskedDevice = { ...device };

            if (device.type === 'EMAIL' && device.email) {
                maskedDevice.email = device.email.replace(
                    /(.{2})([^@]*)(@.+)/,
                    (_, a, b, c) => `${a}${'*'.repeat(b.length)}${c}`
                );
            }

            if (['SMS', 'VOICE'].includes(device.type) && device.phone) {
                maskedDevice.phone = `***-***-${device.phone.slice(-4)}`;
            }

            return maskedDevice;
        });

        const outOfSyncDevices = usableDevices.filter(device => {
            if (device.type === 'EMAIL' && device.email) {
                return !userEmails.includes(device.email.toLowerCase());
            }
            if (['SMS', 'VOICE'].includes(device.type) && device.phone) {
                return !userPhones.includes(device.phone);
            }
            return false;
        });

        const updateRequired = outOfSyncDevices.length > 0;

        const enrolledEmails = usableDevices
            .filter(d => d.type === 'EMAIL' && d.email)
            .map(d => d.email.toLowerCase());

        const enrolledPhoneMethods = usableDevices
            .filter(d => ['SMS', 'VOICE'].includes(d.type) && d.phone)
            .map(d => `${d.type}|${d.phone}`);

        const availableDevices = [];

        userEmails.forEach(email => {
            const normalizedEmail = email.toLowerCase();
            if (!enrolledEmails.includes(normalizedEmail)) {
                availableDevices.push({
                    type: "EMAIL",
                    email,
                    maskedEmail: email && email.includes('@')
                        ? email.replace(/(.{2})([^@]*)(@.+)/, (_, a, b, c) => `${a}${'*'.repeat(b.length)}${c}`)
                        : email,
                    buttonValue: `EMAIL|${email}`
                });
            }
        });

        userPhones.forEach(phone => {
            ['SMS', 'VOICE'].forEach(method => {
                const key = `${method}|${phone}`;
                if (!enrolledPhoneMethods.includes(key)) {
                    availableDevices.push({
                        type: method,
                        phone,
                        maskedPhone: typeof phone === 'string' && phone.length >= 4
                            ? `***-***-${phone.slice(-4)}`
                            : phone,
                        buttonValue: key
                    });
                }
            });
        });

        const allowedEnrollmentMethods = Array.from(
            new Set(availableDevices.map(device => device.type))
        ).join(',');

        const canChangeDevice = usableDevices.length > 1;

        return {
            usableDevices,
            maskedUsableDevices,
            count: usableDevices.length,
            canChangeDevice,
            additionalDevicesAvailable: availableDevices.length > 0,
            updateRequired,
            enrollmentRequired: usableDevices.length === 0,
            allowedEnrollmentMethods,
            availableDevices
        };

    } catch (error) {
        console.error('Error: ', error.message);
        throw new Error(error.message);
    }
}

