        iframe.setAttribute('sandbox', 'allow-scripts allow-same-origin');

        targetDoc.body.insertBefore(overlay, targetDoc.body.firstChild);
        targetDoc.body.insertBefore(iframe, targetDoc.body.firstChild);
    }

    /**
