let autoSubmitTimer = null;
let isAutoSubmitting = false;

codeInput.addEventListener('input', function () {
  const value = codeInput.value.replace(/\D/g, '').slice(0, 6);
  codeInput.value = value;

  if (autoSubmitTimer) {
    clearTimeout(autoSubmitTimer);
    autoSubmitTimer = null;
  }

  if (value.length === 6) {
    isAutoSubmitting = true;

    autoSubmitTimer = setTimeout(() => {
      autoSubmitBtn.click();
      console.log('autosubmit executed');

      //  Disable lower-priority actions
      if (ResendButton) {
        ResendButton.disabled = true;
        ResendButton.classList.add('disabled');
      }
      if (chooseAnotherBtn) {
        chooseAnotherBtn.disabled = true;
        chooseAnotherBtn.classList.add('disabled');
      }

      //  Re-enable after delay
      setTimeout(() => {
        if (ResendButton) {
          ResendButton.disabled = false;
          ResendButton.classList.remove('disabled');
        }
        if (chooseAnotherBtn) {
          chooseAnotherBtn.disabled = false;
          chooseAnotherBtn.classList.remove('disabled');
        }
        console.log('buttons re-enabled');
      }, 1000);

      isAutoSubmitting = false;
      autoSubmitTimer = null;
    }, 1000);
  }

  showErrorMessage();
});
function blockIfAutoSubmitting(event, actionName) {
  if (isAutoSubmitting) {
    event.preventDefault();
    event.stopPropagation();
    event.stopImmediatePropagation();
    console.log(`${actionName} blocked due to autosubmit`);
    return true;
  }
  return false;
}

if (ResendButton) {
  ResendButton.addEventListener('click', function (event) {

    if (blockIfAutoSubmitting(event, 'Resend')) return;

    const codeTextbox = document.getElementById('codetextbox');
    if (codeTextbox) {
      codeTextbox.value = "";
    }

    console.log('resend executed');
    showNewCodeMessage();
  });
}

if (chooseAnotherBtn) {
  chooseAnotherBtn.addEventListener('click', function (event) {

    if (blockIfAutoSubmitting(event, 'Choose Another Method')) return;

    console.log('choose another method executed');
    // normal flow continues here
  });
}








// New js for background


/**
 * Creates and injects a dimmed, full-screen iframe background.
 * Checks if elements already exist to avoid duplicates.
 * @param {string} url - The URL for the background website.
 */
function insertBackgroundSiteDimmed(url) {
  if (document.getElementById(BACKGROUND_IFRAME_ID)) return;

  // Create the dimming overlay
  const overlay = document.createElement('div');
  overlay.id = BACKGROUND_OVERLAY_ID;
  overlay.style.cssText = `
      position:fixed; top:0; left:0; width:100vw; height:100vh;
      background:rgba(0,0,0,0.8); z-index:-1;
    `;

  // Create the iframe
  const iframe = document.createElement('iframe');
  iframe.id = BACKGROUND_IFRAME_ID;
  iframe.src = url;
  iframe.style.cssText = `
      position:fixed; top:0; left:0; width:100vw; height:100vh;
      border:none; z-index:-2;
    `;
  iframe.setAttribute('sandbox', 'allow-scripts allow-same-origin');
 iframe.setAttribute("aria-hidden", "true");
iframe.setAttribute("tabindex", "-1");

  // Add them to the page
  document.body.insertBefore(overlay, document.body.firstChild);
  document.body.insertBefore(iframe, document.body.firstChild);

  console.log("Dimmed background and iframe have been injected.");
}



