function displayData(data, container) {
  container.innerHTML = ""; // Clear existing content
  const groupName = "selectedDevice";

  // Define priority order
  const typePriority = {
    TOTP: 1,
    EMAIL: 2,
    SMS: 3,
    CALL: 4
  };

  // Convert object to array and sort
  const sortedData = Object.keys(data)
    .map(key => ({ key, ...data[key] }))
    .sort((a, b) => {
      return (typePriority[a.type] || 99) - (typePriority[b.type] || 99);
    });

  // Render in sorted order
  for (const item of sortedData) {
    const div = document.createElement("div");
    div.classList.add("option");

    const radio = document.createElement("input");
    radio.type = "radio";
    radio.name = groupName;
    radio.id = `mfa-${item.key}`;
    radio.className = "GlobalLinkNoTx";
    radio.value = item.id;

    radio.addEventListener("change", () => {
      document.getElementById("sendcode").disabled = false;

      const errorMessageDiv = document.getElementById("error-message");
      if (errorMessageDiv) {
        errorMessageDiv.textContent = "";
        errorMessageDiv.style.display = "none";
      }
    });

    const label = document.createElement("label");
    label.htmlFor = radio.id;
    label.className = "transparent-btn";

    if (item.type === "TOTP") {
      label.textContent = item.nickname?.trim()
        ? item.nickname
        : "Authenticator App";
    } else if (item.type === "EMAIL") {
      label.textContent = "Email " + item.email;
    } else if (item.type === "SMS") {
      label.textContent = "Text " + item.phone;
    } else {
      label.textContent = "Call " + item.phone;
    }

    div.appendChild(radio);
    div.appendChild(label);
    container.appendChild(div);
  }
}




