module.exports = a = async ({ params }) => {
    console.log('params: ', params)

    try {
        if (!params.domains || typeof params.domains !== 'string') {
            throw new Error("Missing or invalid 'domains' input");
        }

        const domainList = params.domains
            .split(',')
            .map(d => d.trim().toLowerCase())
            .filter(d => d.length > 0);

        let emailResult = "";
        let filteredEmailList = [];

        if (params.email && typeof params.email === 'string') {
            const emailDomain = params.email.split('@')[1]?.toLowerCase();
            if (emailDomain && domainList.includes(emailDomain)) {
                emailResult = params.email;
            }
        }

        if (Array.isArray(params.emailList)) {
            filteredEmailList = params.emailList.filter(email => {
                if (typeof email !== 'string') return false;
                const domain = email.split('@')[1]?.toLowerCase();
                return domain && domainList.includes(domain);
            });
        }

        return {
            email: emailResult,
            emailList: filteredEmailList
        };

    } catch (err) {
        console.error('Error:', err.message);
        throw new Error(`Email domain filtering failed: ${err.message}`);
    }
}

