module.exports = a = async ({ params }) => {
	let id = params.id

	if (id) {
		var selected = JSON.parse(params.devices).filter(device => {
			return device.id == params.id;
		});

		if (selected && selected.length > 0 && selected[0].hasOwnProperty("nickname")) {
			var deviceName = selected[0].nickname;
		}

		if (selected[0].model) {
			deviceName = selected[0].model.marketingName;
		}

		if (selected[0].displayName) {
			deviceName = selected[0].displayName;
		}

		return {
			selectedDeviceType: selected[0].type,
			selectedDeviceEmail: selected[0].email,
			selectedDevicePhone: selected[0].phone,
			selectedDeviceContact: selected[0].email || selected[0].phone,
			selectedDeviceName: deviceName,
			selectedDeviceOtpEnabled: selected[0].otpEnabled
		}
	}
}

module.exports = a = async ({params}) => {
    console.log('params: ', params)

    try {
        const devices = params.maskedUsableDevices

        if (!Array.isArray(devices)) {
            throw new Error('Invalid input: maskedUsableDevices must be an array')
        }

        if (devices.length !== 1) {
            throw new Error('Multiple Devices Detected')
        }

        const device = devices[0]

        if (!device.id || !device.type) {
            throw new Error('Device is missing required fields: id and type')
        }

        let maskedContact

        switch (device.type) {
            case 'EMAIL':
                maskedContact = device.email || null
                break
            case 'TOTP':
                maskedContact = device.nickname || null
                break
            case 'SMS':
            case 'VOICE':
                maskedContact = device.phone || null // Adapt if structure includes phone
                break
            default:
                maskedContact = null
        }

        return {
            deviceId: device.id,
            deviceType: device.type,
            maskedContact
        }

    } catch (error) {
        console.error('Error processing devices:', error)
        throw error
    }
}

