const newCodeMsg = document.querySelector('.login--NewCodeHasBeenSent');
const errorMsg = document.getElementById('feedbackError');

let newCodeTimer;
let errorTimer;

// Show success message and start 3-minute timer
function showNewCodeMessage() {
    if (newCodeMsg) {
        newCodeMsg.style.display = "flex";
        clearTimeout(newCodeTimer);

        newCodeTimer = setTimeout(() => {
            newCodeMsg.style.display = "none";
        }, 6000); 
    }


    if (errorMsg) {
        errorMsg.style.display = "none";
    }
}

// Show error message and start 3-minute timer
function showErrorMessage() {
    if (errorMsg) {
        errorMsg.style.display = "block";
         clearTimeout(errorTimer);
        clearTimeout(newCodeTimer); // stop success timer if running

        errorTimer = setTimeout(() => {
            errorMsg.style.display = "none";
        }, 6000); 
    }


    if (newCodeMsg) {
        newCodeMsg.style.display = "none";
    }
}

