module.exports = a = async ({params}) => {
    console.log('params: ', params)

    const { inputA, inputB } = params

    // Validate inputs
    if (typeof inputA !== 'number' || typeof inputB !== 'number') {
        throw new Error('Both inputA and inputB must be numbers.')
    }

    // Perform subtraction
    const difference = inputA - inputB

    // Check for disallowed result
    if (difference === 1) {
        throw new Error('The difference between inputA and inputB cannot be 1.')
    }

    // Return result
    return { difference }
}

