//Handle SendCodeClick when Radio button not selected
function handleSendCodeClick(event) {
  const selected = document.querySelector('input[name="selectedDevice"]:checked');
  const errorMessageDiv = document.getElementById("error-message");

  if (!selected) {

    event.preventDefault();
    event.stopPropagation();
    errorMessageDiv.textContent = "Please select a method.";
    console.log("select radio")
    errorMessageDiv.style.color = "#eb0000";
