        const domainList = params.domains
            .split(',')
            .map(d => d.trim().toLowerCase())
            .filter(d => d.length > 0)

        const emailDevices = params.devices.filter(d => 
            d.type === 'EMAIL' && typeof d.email === 'string'
        )

        for (const device of emailDevices) {
            const emailDomain = device.email.split('@')[1]?.toLowerCase()
            if (!emailDomain) continue
            if (!domainList.includes(emailDomain)) {
                return { deviceId: device.id }
            }
        }

        throw new Error("No EMAIL device found with a non-matching domain")
    } catch (err) {
        console.error('Error: ', err)
        throw new Error(err.message)
    }
}

