module.exports = a = async ({ params }) => {
    console.log('params: ', params)

    try {
        if (!params.usableDevices || !params.allowedDeviceTypes || !params.webAuthenSupport) {
            throw new Error("Missing required parameters: usableDevices, allowedDeviceTypes, or webAuthenSupport");
        }

        let usableDevices = JSON.parse(params.usableDevices);
        if (!Array.isArray(usableDevices)) {
            throw new Error("usableDevices must be a JSON array string");
        }

        const allowedDeviceTypes = params.allowedDeviceTypes.split(",");
        const allowSMS = allowedDeviceTypes.includes("SMS");
        const allowEmail = allowedDeviceTypes.includes("EMAIL");
        const allowTOTP = allowedDeviceTypes.includes("TOTP");
        const allowFIDO2 = allowedDeviceTypes.includes("FIDO2");
        const allowVOICE = allowedDeviceTypes.includes("VOICE");

        // Remove FIDO-related devices if no WebAuthn support
        if (params.webAuthenSupport === "NONE") {
            usableDevices = usableDevices.filter(device => {
                return device !== "SECURITY_KEY" && device !== "PLATFORM" && device !== "FIDO2";
            });
        }

        // Filter to only allowed devices
        usableDevices = usableDevices.filter(device => {
            return (device === "SMS" && allowSMS)
                || (device === "EMAIL" && allowEmail)
                || (device === "TOTP" && allowTOTP)
                || (device === "FIDO2" && allowFIDO2)
                || (device === "VOICE" && allowVOICE);
        });

        // Remove PLATFORM if only security key is supported
        if (params.webAuthenSupport === "SECURITY_KEY_ONLY") {
            usableDevices = usableDevices.filter(device => device !== "PLATFORM");
        }

        // Optional: Prioritize certain devices
        const reorderPriority = ["FIDO2", "SMS", "VOICE"]; // Prioritization order
        for (const type of reorderPriority.reverse()) {
            if (usableDevices.includes(type)) {
                const index = usableDevices.indexOf(type);
                if (index !== -1) {
                    usableDevices.splice(index, 1);
                    usableDevices.unshift(type);
                }
            }
        }

        return {
            usableDevices,
            count: usableDevices.length
        };

    } catch (error) {
        console.error('Error:', error.message);
        throw new Error(`Device filtering failed: ${error.message}`);
    }
}

