module.exports = a = async ({params}) => {
    let email = params.email;
    let phone = params.phone;

    if (email && email.includes('@')) {
        const [username, domain] = email.split('@');

        if (username.length <= 2) {
            email = username + '@' + domain;
        } else {
            const visible = username.slice(0, 2);
            const masked = '*'.repeat(username.length - 2);
            email = visible + masked + '@' + domain;
        }
    }

    if (phone) {
        // Strip all non-numeric characters from the phone number
        const numericPhoneNumber = phone.replace(/\D/g, '');
        // Mask the first six digits of the phone number
        phone = '***-***-' + numericPhoneNumber.slice(-4);
    }

    return { 'maskedEmail': email, 'maskedPhone': phone };
}

