// Handle radio selection and reveal inputs
function hideNoSelectError() {
  if (!errorMessageDivSelect) return;
  errorMessageDivSelect.textContent = "";
  errorMessageDivSelect.style.display = "none";
}

radios.forEach(radio => {
  radio.addEventListener('change', () => {
    hideNoSelectError();
    const selectedValue = radio.value;
    methodInput.value = selectedValue;

    emailInput.style.display = 'none';
    phoneInput.style.display = 'none';
    selectBox.style.display= 'none';
    phoneInputGroup.style.display ="none"

    // --- FIX: Clear both errors when switching methods ---
    const emailError = document.getElementById("emailError");
    const phoneError = document.getElementById("phoneError");

    if (emailError) { emailError.textContent = ""; emailError.style.display = "none"; }
    if (phoneError) { phoneError.textContent = ""; phoneError.style.display = "none"; }
    // // --- FIX: Reset borders ---
    // if (emailInput) emailInput.style.border = "";
    // if (phoneInput) phoneInput.style.border = "";

    if (selectedValue === 'EMAIL') {
      emailInput.style.display = 'block';
      methodValueInput.value = emailInput.value;
    } else if (selectedValue === 'SMS') {
      phoneInput.style.display = 'block';
      selectBox.style.display= 'flex';
      phoneInputGroup.style.display= 'flex';
      methodValueInput.value = phoneInput.value;
    } else {
      methodValueInput.value = ''; // TOTP doesnt require a secondary value
    }

    if (errorMessageDiv) {
      errorMessageDiv.textContent = "";
      errorMessageDiv.style.display = "none";
    }
    disableHiddenInputs();
  });
});

