// --- Validation on Email and Phone ---


function validateEmailAndPhone(event) {
  const selected = document.querySelector('input[name="selectedDevice"]:checked');

  const emailInput = document.getElementById("emailInput");
  const emailError = document.getElementById("emailError");

  const phoneInput = document.getElementById("phoneInput");
  const phoneError = document.getElementById("phoneError");
  let isValid = true;

  // Clear error fields
  if (emailError) { emailError.textContent = ""; emailError.style.display = "none"; }
  if (phoneError) { phoneError.textContent = ""; phoneError.style.display = "none"; }
  // emailInput.style.border = "";
  // phoneInput.style.border = "";
  if (emailInput) emailInput.style.border = "";
  if (phoneInput) phoneInput.style.border = "";


  // --- EMAIL validation ---
  if (selected && selected.value === "EMAIL") {
    const emailValue = emailInput.value.trim();
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!emailValue || !emailPattern.test(emailValue)) {
      event.preventDefault();
      event.stopPropagation();
      emailError.textContent = "Please enter a valid email address.";
      emailError.style.color = "#eb0000";
      emailError.style.fontSize = "12px";
      emailError.style.display = "block";
      emailError.style.whiteSpace = "nowrap";
      emailInput.style.border = "1px solid red";
      isValid = false;
    } else {
      emailInput.style.border = "";
      emailError.style.display = "none";
    }

    // --- LIVE email input validation ---
    if (!emailInput._hasEmailListener) {
      emailInput.addEventListener("input", () => {
        const value = emailInput.value.trim();
        if (emailPattern.test(value)) {
          emailError.textContent = "";
          emailError.style.display = "none";
          emailInput.style.border = "";
        }
      });
      emailInput._hasEmailListener = true;
    }
  }

  // --- PHONE validation ---
  if (selected && selected.value === "SMS") {
      errorMessageDivSelect.textContent = "";
    errorMessageDivSelect.style.display = "none";
    const phoneValue = phoneInput.value.trim();


    const allowedChars = /^[0-9]*$/;   // /^[0-9()+\-\s]*$/;
    const formattedPattern = /^\+\d{1,3}\s?\(\d{3}\)-\(\d{3}\)-\(\d{4}\)$/; ///^\+\d{1,3}\s?\(?\d{3}\)?[-\s]?\(?\d{3}\)?[-\s]?\(?\d{4}\)?$/; 
    const plainPattern = /^\+\d{1,3}[\s]?\d{7,15}$/;

    const phonePattern = /^[0-9]{6,17}$/; // Only digits, length 6-17

    const showPhoneError = (msg) => {
      phoneError.textContent = msg;
      phoneError.style.color = "#eb0000";
      phoneError.style.fontSize = "12px";
      phoneError.style.display = "block";
      phoneError.style.whiteSpace = "nowrap";
      phoneInputGroup.style.border = "1px solid red";
    };

    const clearPhoneError = () => {
      phoneError.textContent = "";
      phoneError.style.display = "none";
      phoneInputGroup.style.border = "";
    };

    // --- LIVE phone input validation ---
    if (!phoneInput._hasPhoneListener) {
      phoneInput.addEventListener("input", (e) => {
        console.log("login enrollment");
        const value = e.target.value;
        // --- LIVE EMPTY FIELD CHECK ---
        if (!value) {
          showPhoneError("Please enter a valid phone number.");
          console.log("phone required.");
          return;
        }
        // --- LIVE DIGIT LENGTH CHECK ---
        const digitCount = value.replace(/\D/g, "");
        if (digitCount.length < 6 || digitCount.length > 17) {
          showPhoneError("Please enter a valid phone number.");
          console.log("Phone number must contain 617 digits.");
          return;
        }
        // --- LIVE ALLOWED CHARS CHECK ---
        if (!allowedChars.test(value)) {
          showPhoneError("Please enter a valid phone number.");
          return;
        }
        // If all good, clear errors
        clearPhoneError();
      });
      phoneInput._hasPhoneListener = true;
    }

    // Validate on submit
    if (!phoneValue || !allowedChars.test(phoneValue)) {
      event.preventDefault();
      event.stopPropagation();
      showPhoneError("Please enter a valid phone number.");
      isValid = false;
      return;
    }

    //+ should not allow
    if (/[+()]/.test(phoneValue)) {
      event.preventDefault();
      event.stopPropagation();
      showPhoneError("Please enter a valid phone number.");
      isValid = false;
      return;
    }

    // Cannot end with a special character
    if (!/[0-9)]$/.test(phoneValue)) {
      event.preventDefault();
      event.stopPropagation();
      showPhoneError("Please enter a valid phone number.");
      isValid = false;
      return;
    }

    // Must match at least one valid pattern
    // if (!formattedPattern.test(phoneValue) && !plainPattern.test(phoneValue)) {
    //   event.preventDefault();
    //   event.stopPropagation();
    //   showPhoneError("Please enter a valid phone number.");
    //   isValid = false;
    // }

    //allow 10-16 digits
    const digitCount = phoneValue.replace(/\D/g, "");
    if (digitCount.length < 6 || digitCount.length > 17) {
      event.preventDefault();
      event.stopPropagation();
      showPhoneError("Please enter a valid phone number.");
      console.log("Phone number must contain 1016 digits.");
      isValid = false;
      return isValid;
    }

    if (isValid) clearPhoneError();
  }

  return isValid;
}




