let skipByCount = "{{global.variables.skipByCount}}";
let maxSkipCount = "{{global.company.variables.maxSkipCount}}";

// Show or hide the skip button based on expiry date
function showSkipButton(skipByCount, maxSkipCount) {

  console.log("Skip By count ", skipByCount);
  console.log("Max Skip count ", maxSkipCount);

  const skipButton = document.getElementById("skipButton");
  if (!skipButton) return; // Exit if button not found


  if (maxSkipCount > skipByCount) {
     skipButton.style.display = "inline-block"; // Show
  } else {
    skipButton.style.display = "none"; // Hide
   
  }
}


// Initial call to show/hide button
showSkipButton(skipByCount, maxSkipCount);

// Optional: log skip
if (skipButton) {
  skipButton.addEventListener("click", function (event) {
    const buttonType = event.target.getAttribute("data-skbuttonvalue");
    console.log("Button clicked:", buttonType);
    if (buttonType === "SKIP") {
      skipByCount = skipByCount + 1; // Increase skip count

      // Re-evaluate skip button visibility
      showSkipButton(skipByCount, maxSkipCount);
    }
  });
}



//background image 

// --- NEW: Unique IDs for our dynamic elements ---
const BACKGROUND_IFRAME_ID = 'dynamic-background-iframe';
const BACKGROUND_OVERLAY_ID = 'dynamic-background-overlay';


const domain = "{{global.variables.domain}}";
console.log("Domain  is:", domain);


// Track whether background is currently active
let backgroundVisible = false;

/**
 * Creates and injects a dimmed, full-screen iframe background.
 * Checks if elements already exist to avoid duplicates.
 * @param {string} url - The URL for the background website.
 */
function insertBackgroundSiteDimmed(url) {
  if (document.getElementById(BACKGROUND_IFRAME_ID)) return;

  // Create the dimming overlay
  const overlay = document.createElement('div');
  overlay.id = BACKGROUND_OVERLAY_ID;
  overlay.style.cssText = `
      position:fixed; top:0; left:0; width:100vw; height:100vh;
      background:rgba(0,0,0,0.8); z-index:-1;
    `;

  // Create the iframe
  const iframe = document.createElement('iframe');
  iframe.id = BACKGROUND_IFRAME_ID;
  iframe.src = url;
