module.exports = a = async ({params}) => {
    console.log('params: ', params)

    try {
        const maxResends = params.maxResends
        const deviceId = params.deviceId
        let currentState = params.currentState

        if (!deviceId || typeof maxResends !== 'number') {
            throw new Error("Missing required parameters: deviceId or maxResends")
        }

        // Normalize currentState
        if (!currentState || currentState === '' || currentState === null) {
            currentState = []
        } else if (typeof currentState === 'string') {
            try {
                currentState = JSON.parse(currentState)
            } catch {
                throw new Error("Invalid currentState format; expected an array or JSON array string.")
            }
        }

        if (!Array.isArray(currentState)) {
            throw new Error("currentState must be an array")
        }

        let found = false
        const updatedState = []

        for (const entry of currentState) {
            const [id, countStr] = entry.split('|')
            const count = parseInt(countStr, 10)

            if (id === deviceId) {
                found = true
                if (count >= maxResends) {
                    throw new Error("maxResends Reached")
                }
                updatedState.push(`${deviceId}|${count + 1}`)
            } else {
                updatedState.push(entry)
            }
        }

        if (!found) {
            updatedState.push(`${deviceId}|1`)
        }

        console.log('updatedState: ', updatedState)
        return { updatedState }
    } catch (error) {
        console.error('Error in MFA resend counter:', error.message)
        throw error
    }
}

