         if(description) description.style.display = 'none';
        errorMessage.style.display = 'none';
        break;
    }
  });
});

// ================= LIVE VALIDATION =================

// ---------- EMAIL LIVE VALIDATION ----------
if (emailInput) {
  emailInput.addEventListener("input", () => {
    const value = emailInput.value.trim();
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!value) {
      emailError.textContent = "Please enter your email address.";
      emailError.style.display = "block";
      emailInput.style.border = "1px solid red";
      return;
    }

    if (!emailPattern.test(value)) {
      emailError.textContent = "Please enter a valid email address.";
      emailError.style.display = "block";
      emailInput.style.border = "1px solid red";
      return;
    }

    emailError.textContent = "";
    emailError.style.display = "none";
    emailInput.style.border = "";
  });
}


// ---------- PHONE LIVE VALIDATION ----------
if (phoneInput) {
  const phonePattern = /^\d+$/; ///^\+?\d+$/;

  phoneInput.addEventListener("input", () => {
    const value = phoneInput.value.trim();

    if (!value) {
      phoneError.textContent = "Please enter valid phone number.";
      phoneError.style.display = "block";
      phoneInputGroup.style.border = "1px solid red";
      return;
    }

    // Only digits and optional + at start
    if (!phonePattern.test(value)) {
      phoneError.textContent = "Please enter valid phone number.";
      phoneError.style.display = "block";
      phoneInputGroup.style.border = "1px solid red";
      return;
    }

    const digitsOnly = value.replace(/\D/g, "");
    if (digitsOnly.length < 6 || digitsOnly.length > 17) {
      phoneError.textContent = "Please enter valid phone number.";
      phoneError.style.display = "block";
      phoneInputGroup.style.border = "1px solid red";
      return;
    }

    phoneError.textContent = "";
    phoneError.style.display = "none";
    phoneInputGroup.style.border = "";
  });
}


function handleSendCodeClick(event) {
  const selected = document.querySelector('input[name="authMethod"]:checked');
  const errorMessageDiv = document.getElementById("error-message");

  const emailInput = document.getElementById("emailInput");
  const emailError = document.getElementById("emailError");

  const phoneInput = document.getElementById("phoneInput");
  const phoneError = document.getElementById("phoneError");
  const phoneInputGroup = document.getElementById('phoneInputGroup');

  let isValid = false;

  // Clear previous error messages
  errorMessageDiv.textContent = "";
  errorMessageDiv.style.display = "none";
  emailError.textContent = "";
  emailError.style.display = "none";
  phoneError.textContent = "";
  phoneError.style.display = "none";

  // Check if no auth method is selected
  if (!selected) {
    event.preventDefault();
    event.stopPropagation();
    isValid = false;
    errorMessageDiv.textContent = "Please select a method.";
    errorMessageDiv.style.color = "#eb0000";
    errorMessageDiv.style.fontFamily = "Roboto-Regular, sans-serif";
    errorMessageDiv.style.textAlign = "left";
    errorMessageDiv.style.fontSize = "12px";
    errorMessageDiv.style.lineHeight = "18px";
    errorMessageDiv.style.fontWeight = "400";
    errorMessageDiv.style.marginLeft = "15px";
    errorMessageDiv.style.display = "block";
    return;
  }

  // Email validation
  if (selected.value === "email") {
    const emailValue = emailInput.value.trim();
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!emailValue) {
      event.preventDefault();
      event.stopPropagation();
      isValid = false;
      emailError.textContent = "Please enter a valid email address.";
      emailError.style.color = "#eb0000";
      emailError.style.fontSize = "12px";
      emailError.style.display = "block";
      emailInput.style.border = "1px solid red"; // add red border
      return;
    } else {
      // Clear email error when typing
      emailInput.addEventListener('input', () => {
        if (emailInput.value.trim() !== "") {
          emailError.textContent = "";
          emailError.style.display = "none";
          console.log("EmailInput", emailInput.value);
          emailInput.style.border = "";
        }
      });
    }

    if (!emailPattern.test(emailValue)) {
      event.preventDefault();
      event.stopPropagation();
      isValid = false;
      emailError.textContent = "Please enter a valid email address.";
      emailError.style.color = "#eb0000";
      emailError.style.fontSize = "12px";
      emailError.style.display = "block";
      emailInput.style.border = "1px solid red"; // add red border
      return;
    }
    emailInput.style.border = "";
  }

  if (selected.value === "phone") {
  const showError = (msg) => {
    phoneError.textContent = msg;
    phoneError.style.color = "#eb0000";
    phoneError.style.fontSize = "12px";
    phoneError.style.display = "block";
    phoneInputGroup.style.border = "1px solid red";
  };

  const clearError = () => {
    phoneError.textContent = "";
    phoneError.style.display = "none";
    phoneInputGroup.style.border = "";
  };

  //  Allow optional + at start, digits only after
  const phonePattern = /^\+?\d+$/;

  const handleLiveValidation = (e) => {
    const value = e.target.value.trim();

    if (!value) {
      showError("Please enter valid phone number.");
      return;
    }

    //  invalid characters or + not at start
    if (!phonePattern.test(value)) {
      showError("Please enter valid phone number.");
      return;
    }

    // Remove + for length validation
    const digitsOnly = value.replace(/\D/g, "");

    if (digitsOnly.length < 6 || digitsOnly.length > 17) {
      showError("Please enter valid phone number.");
      console.log("minlength max length");
      return;
    }

    //  valid
    clearError();
  };

  // Avoid duplicate listeners
  if (!phoneInput._hasPhoneListener) {
    phoneInput.addEventListener("input", handleLiveValidation);
    phoneInput._hasPhoneListener = true;
  }

  // ---------- FINAL SUBMIT VALIDATION ----------
  const phoneValue = phoneInput.value.trim();

  if (!phoneValue) {
    isValid = false;
    showError("Please enter valid phone number.");
    return;
  }

  if (!phonePattern.test(phoneValue)) {
    isValid = false;
    showError("Please enter valid phone number.");
    return;
  }

  const digitsOnly = phoneValue.replace(/\D/g, "");

  if (digitsOnly.length < 6 || digitsOnly.length > 17) {
    isValid = false;
    showError("Please enter valid phone number.");
    return;
  }


  clearError();
  
    // --- Ensure countryCode is preserved ---
  const countryCodeInput = document.getElementById("countryCode");
  const selectedOption = document.querySelector(".option.selected");

  if (countryCodeInput && selectedOption) {
    const iso = selectedOption.dataset.iso;
    const country = countryData.find(c => c.iso2 === iso);
    if (country) {
      countryCodeInput.value = country.dialCode;
    }
  }

}


}

