module.exports = a = async ({params}) => {
	let errorDetails = {};

	// Handle hardcoded error details passed as string
	if(typeof(params.errorDetails) === "string") errorDetails.message = params.errorDetails;
	// If error details passed is of type object
	else errorDetails = params.errorDetails;

	// Add companyId & interactionId if it is missing
	if(!errorDetails.companyId) errorDetails = {'companyId': `{{global.companyId}}`, ...errorDetails};
	if(!errorDetails.interactionId) errorDetails = {'interactionId': `{{global.interactionId}}`, ...errorDetails};
	// Add the flowId property at the beginning of the error object
	if(!errorDetails.flowId) errorDetails = {'flowId': `{{global.flowId}}`, ...errorDetails};
	if(!errorDetails.message) errorDetails.message = params.errorMessage;

	return {'errorDetails': errorDetails};
}

